import React, { useState, useEffect } from 'react';
import axios from 'axios';

const Dashboard = () => {
  const [description, setDescription] = useState('');
  const [timer, setTimer] = useState(0);
  const [link, setLink] = useState('');
  const [visible, setVisible] = useState(false);

  useEffect(() => {
    // Fetch current banner settings
    axios.get('http://localhost:5000/api/banner')
      .then(response => {
        const { description, timer, link, visible } = response.data;
        setDescription(description);
        setTimer(timer);
        setLink(link);
        setVisible(visible);
      })
      .catch(error => console.error('Error fetching banner settings:', error));
  }, []);
  const show = () => {
    
  }

  const handleSubmit = () => {
    axios.post('http://localhost:5000/api/banner', {
      description,
      timer,
      link,
      visible
    })
    .then(response => alert('Banner settings updated'))
    .catch(error => console.error('Error updating banner settings:', error));
    axios.get('http://localhost:5000/api/banner')
      .then(response => {
        const { description, timer, link, visible } = response.data;
        setDescription(description);
        setTimer(timer);
        setLink(link);
        setVisible(visible);
      })
      .catch(error => console.error('Error fetching banner settings:', error));
  };

  return (
    <div className="dashboard">
      <h2>Banner Settings</h2>
      <label>
        Description:
        <input
          type="text"
          value={description}
          onChange={(e) => setDescription(e.target.value)}
        />
      </label>
      <label>
        Timer (seconds):
        <input
          type="number"
          value={timer}
          onChange={(e) => setTimer(e.target.value)}
        />
      </label>
      <label>
        Link:
        <input
          type="text"
          value={link}
          onChange={(e) => setLink(e.target.value)}
        />
      </label>
      <label>
        Visible:
        <input
          type="checkbox"
          checked={visible}
          onChange={(e) => setVisible(e.target.checked)}
        />
      </label>
      <button onClick={handleSubmit}>Save Settings</button>
    </div>
  );
};

export default Dashboard;
