import React, { useState } from 'react';
import axios from 'axios';
import { TextField, Button, Container, Typography, Checkbox, FormControlLabel } from '@mui/material';

const Dashboard = ({ onBannerUpdate }) => {
    const [description, setDescription] = useState('');
    const [timer, setTimer] = useState(0);
    const [link, setLink] = useState('');
    const [visible, setVisible] = useState(true);

    const handleSubmit = (event) => {
        event.preventDefault();
        axios.put('http://localhost:5000/api/banner', { description, timer, link, visible })
            .then(response => {
                // Update the parent component with new banner data
                setBanner(response.data);
                onBannerUpdate(response.data);
            })
            .catch(error => {
                console.error('Error updating banner data', error);
            });
    };

    return (
        <Container maxWidth="sm">
            <Typography variant="h5" gutterBottom>Update Banner Settings</Typography>
            <form onSubmit={handleSubmit}>
                <TextField
                    label="Description"
                    fullWidth
                    value={description}
                    onChange={(e) => setDescription(e.target.value)}
                    margin="normal"
                />
                <TextField
                    label="Timer (seconds)"
                    type="number"
                    fullWidth
                    value={timer}
                    onChange={(e) => setTimer(Number(e.target.value))}
                    margin="normal"
                />
                <TextField
                    label="Link"
                    fullWidth
                    value={link}
                    onChange={(e) => setLink(e.target.value)}
                    margin="normal"
                />
                <FormControlLabel
                    control={<Checkbox checked={visible} onChange={() => setVisible(!visible)} />}
                    label="Show Banner"
                />
                <Button
                    variant="contained"
                    color="primary"
                    type="submit"
                    style={{ marginTop: '20px' }}
                    onClick={handleSubmit}
                >
                    Save Settings
                </Button>
            </form>
        </Container>
    );
};

export default Dashboard;
