import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { TextField, Button, Container, Typography, Checkbox, FormControlLabel } from '@mui/material';

const Dashboard = () => {
    const [description, setDescription] = useState('');
    const [timer, setTimer] = useState(0);
    const [link, setLink] = useState('');
    const [visible, setVisible] = useState(true);
    const [banner, setBanner] = useState(null);

    useEffect(() => {
        // Fetch the current banner settings
        axios.get('http://localhost:5000/api/banner')
            .then(response => {
                const { description, timer, link, visible } = response.data;
                setDescription(description);
                setTimer(timer);
                setLink(link);
                setVisible(visible);
                setBanner(response.data);
            })
            .catch(error => {
                console.error('Error fetching banner data', error);
            });
    }, []);

    const handleSubmit = (event) => {
        event.preventDefault();
        axios.put('http://localhost:5000/api/banner', { description, timer, link, visible })
            .then(response => {
                setBanner(response.data); // Update local state with the updated banner data
                console.log('Banner updated');
            })
            .catch(error => {
                console.error('Error updating banner data', error);
            });
    };

    return (
        <Container maxWidth="sm">
            <Typography variant="h5" gutterBottom>Update Banner Settings</Typography>
            <form onSubmit={handleSubmit}>
                <TextField
                    label="Description"
                    fullWidth
                    value={description}
                    onChange={(e) => setDescription(e.target.value)}
                    margin="normal"
                />
                <TextField
                    label="Timer (seconds)"
                    type="number"
                    fullWidth
                    value={timer}
                    onChange={(e) => setTimer(Number(e.target.value))}
                    margin="normal"
                />
                <TextField
                    label="Link"
                    fullWidth
                    value={link}
                    onChange={(e) => setLink(e.target.value)}
                    margin="normal"
                />
                <FormControlLabel
                    control={<Checkbox checked={visible} onChange={() => setVisible(!visible)} />}
                    label="Show Banner"
                />
                <Button
                    variant="contained"
                    color="primary"
                    type="submit"
                    style={{ marginTop: '20px' }}
                >
                    Save Settings
                </Button>
            </form>
        </Container>
    );
};

export default Dashboard;
